// src/ProductTable.js
import React, { useEffect, useState } from "react";
import firebase from "firebase/app";
import "firebase/database";

const ProductTable = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Initialize Firebase with the configuration from firebaseConfig.js
    firebase.initializeApp(firebaseConfig);

    // Get a reference to the 'products' collection in Firebase
    const productsRef = firebase.database().ref("products");

    // Listen for changes in the data and update the state
    productsRef.on("value", (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const productList = Object.values(data);
        setProducts(productList);
      }
    });

    // Clean up the listener when the component unmounts
    return () => productsRef.off("value");
  }, []);

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Price</th>
            <th>Reviews</th>
            <th>Link</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.ASIN}>
              <td>{product.Title}</td>
              <td>{product.Price}</td>
              <td>{product.Reviews}</td>
              <td>
                <a href={product.Link} target="_blank" rel="noopener noreferrer">
                  View on Amazon
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTable;
